mapper.select:
SELECT rno,rname,rtype,rscore,rscorecount,rarea,raddr1,rtel,ilink,ropentime,rclosetime,num FROM (SELECT rno,rname,rtype,rscore,rscorecount,rarea,raddr1,rtel,ilink,ropentime,rclosetime,rownum as num FROM (SELECT maininfo.rno,rname,rtype,rscore,rscorecount,rarea,raddr1,rtel,ilink,ropentime,rclosetime FROM maininfo,image,reserveinfo,subinfo WHERE maininfo.rno=image.rno AND maininfo.rno=reserveinfo.rno AND maininfo.rno=subinfo.rno AND image.iname LIKE 'Mainimage'
AND rtype LIKE '%'||#{filter1}||'%'
AND rOpentime <= #{today} AND rClosetime > #{today}
AND rDelivery LIKE #{filter4}
ORDER BY rScore DESC
ORDER BY rGood DESC
ORDER BY rHit DESC
ORDER BY rHighPrice DESC
ORDER BY rLowPrice ASC
)) WHERE num BETWEEN #{start} AND #{end}

	SELECT CEIL(COUNT(*)/15.0) 
	FROM maininfo,image,reserveinfo,subinfo
	WHERE maininfo.rno=image.rno
  AND maininfo.rno=reserveinfo.rno
  AND maininfo.rno=subinfo.rno
  AND image.iname LIKE 'Mainimage'

    AND rtype LIKE '%'||#{filter1}||'%'



    AND rOpentime <= #{today}
    AND rClosetime > #{today}



    AND rDelivery LIKE #{filter4}
